function moveToTargetDiv(target, outer ,layout){
	var out = $(outer);
	var tar = $(target);
	//var x = out.width();
	//var y = tar.outerWidth(true);
	var z = tar.index();
	var q = 0;
	var m = out.find('li');

    for(var i = 0; i < z; i++){
          q+= $(m[i]).outerWidth(true)+4;
    }

	$('.tabs--scroll ul').animate({
		scrollLeft: Math.max(0, q )
	}, 800);
	return false;
}

function moveToTargetDivssss(target, outer ,layout){
	var out = $(outer);
	var tar = $(target);
	var z = tar.index();
	var m = out.find('li');

	if(layout == 'ltr'){
		var q = 0;
		for(var i = 0; i < z; i++){
			q+= $(m[i]).outerWidth(true)+4;
		}
	}else{
		var ulWidth = 0;
		$(outer+" li").each(function() {
			ulWidth = ulWidth + $(this).outerWidth(true);
		});

		var q = 0;
		for(var i = 0; i <= z; i++){
			q+= $(m[i]).outerWidth(true);
		}
		q = ulWidth - q;

		/* var q = out.last().outerWidth(true);
		var q = ulWidth;
		for(var i = z; i > 0; i--){
			q-= $(m[i]).outerWidth(true);
		}   */
	}
	out.animate({
		scrollLeft: Math.max(0,q )
	}, 800);
	return false;
}

function getCookie(cname) {
  var name = cname + "=";
  var ca = document.cookie.split(';');
  for(var i = 0; i < ca.length; i++) {
    var c = ca[i];
    while (c.charAt(0) == ' ') {
      c = c.substring(1);
    }
    if (c.indexOf(name) == 0) {
      return c.substring(name.length, c.length);
    }
  }
  return "";
}
